import React from 'react';
import { render, screen, fireEvent } from '@testing-library/react';
import '@testing-library/jest-dom/extend-expect';
import Generate from '../../Generate.jsx';

describe('Generate component', () => {
  test('renders without crashing', () => {
    render(<Generate />);
    expect(screen.getByText('Generate Here')).toBeInTheDocument();
  });

  test('input fields can be updated', () => {
    render(<Generate />);
    const nameInput = screen.getByPlaceholderText('Enter Name');
    fireEvent.change(nameInput, { target: { value: 'John Doe' } });
    expect(nameInput.value).toBe('John Doe');
    
    const dayInput = screen.getByPlaceholderText('Enter Day');
    fireEvent.change(dayInput, { target: { value: 24 } });
    expect(dayInput.value).toBe('24');
  });

  test('link generation functionality', () => {
    render(<Generate />);
    const nameInput = screen.getByPlaceholderText('Enter Name');
    fireEvent.change(nameInput, { target: { value: 'Jane' } });
    const dayInput = screen.getByPlaceholderText('Enter Day');
    fireEvent.change(dayInput, { target: { value: '12' } });
    const monthSelect = screen.getByDisplayValue('January');
    fireEvent.change(monthSelect, { target: { value: '5' } });

    fireEvent.click(screen.getByText('Generate Link'));
    expect(screen.getByText('https://birthday-wisher.netlify.app/birthday/Jane/12/5')).toBeInTheDocument();
  });

  test('generated link display', () => {
    render(<Generate />);
    fireEvent.click(screen.getByText('Generate Link'));
    const linkElement = screen.queryByText(/^https:\/\/birthday-wisher.netlify.app\/birthday\//);
    expect(linkElement).not.toBeNull();
  });
});

